mapper.update:
UPDATE routine_tasks



            WHEN id = #{task.id} AND routine_id = #{task.routineId} THEN #{task.orderIndex}




            WHEN id = #{task.id} AND routine_id = #{task.routineId} THEN #{task.isChecked}



WHERE (id, routine_id) IN

    (#{task.id}, #{task.routineId})

mapper.delete:
DELETE FROM routine_tasks
WHERE routine_id = #{routineId}
  AND id IN

    #{id}
